package Javaprograms;

public class Swap 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		
		int a=1;
		int b=2;
		int temp;
		
		System.out.println("Value of a="+a+" and b="+b+" before swapping");
		
		temp=a;
		a=b;
		b=temp;
		
		System.out.println("Value of a="+a+" and b="+b+" after swapping");
	}

}
