package Javaprograms;

public class Swapwithouttemp 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub

		int a=10;
		int b=5;
		System.out.println("Value of a and b "+ a+" "+b);
		
		a= a+b;//15
		b=a-b;//10
		a=a-b;//15-10 =5
		System.out.println("Value of a and b "+ a+" "+b);
		
				
	}

}
