package Javaprograms;

public class Swapwithouttemp2 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub

		int a=10;
		int b=5;
		
		System.out.println("Value of a "+a+" Value of b "+b);
		a = a*b;//50
		b = a/b;//10
		a= a/b;//5
		System.out.println("Value of a "+a+" Value of b "+b);
	}

}
