package Javaprograms;

public class printsequence 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub

		//1,2,4,7,11,16
//1+1=2 2+2=4  4+3=7 7+4=11 11+5=16 
		int i=1;
		int n = 6;
		for(int j=0;j<n;j++)//1247
		{
			
			i=i+j;
			
			System.out.println(i);
			
		}
	
	}

}
