package Practice;

public class swapnumbers 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub

		int a =5;
		int b =10;
		
		System.out.println("Before swapping=="+a+"=="+b);
	
		 a=a+b;//15
		b=a-b;//5
		a=a-b;//15-5=10
		
		System.out.println("After swapping=="+a+"=="+b);
		
		
	
	}

}
